(()=>{"use strict";const t=document.getElementById("app");function n(){const t=document.querySelectorAll(".section"),n=window.innerHeight;t.forEach((t=>{t.getBoundingClientRect().top<n-100&&t.classList.add("visible")}))}[{id:1,title:"Welcome to My Portfolio",content:'\n      <p class="intro-text">\n        Hello, I\'m <strong class="highlight">Nicolas Aguilera</strong>, a passionate \n        <span class="highlight">Software Developer</span>. I specialize in creating intuitive and engaging user interfaces using HTML, CSS, and JavaScript.\n      </p>\n    '},{id:2,title:"About Me",content:'\n      <p class="about-text">\n        I\'m a <span class="highlight">Software Development Technician</span> graduate from TECLAB Technical Institute in Argentina.\n      </p>\n    '},{id:3,title:"My Projects",content:'\n      <ul class="project-list">\n        <li><strong class="highlight">RUF Website Update (Internship)</strong></li>\n        <li><strong class="highlight">Web Development Practices</strong></li>\n      </ul>\n    '},{id:4,title:"My Skills",content:'\n      <ul class="skills-list">\n        <li><strong class="highlight">Programming Languages:</strong> Java, C++, JavaScript, HTML, CSS.</li>\n        <li><strong class="highlight">Frameworks:</strong> React, jQuery.</li>\n      </ul>\n    '},{id:5,title:"Contact Me",content:'\n      <p class="contact-text">You can contact me via email or phone.</p>\n    '}].forEach((n=>{const e=document.createElement("div");e.className="section",e.innerHTML=`\n    <h2>${n.title}</h2>\n    ${n.content}\n  `,t.appendChild(e)})),window.addEventListener("scroll",n),n(),window.addEventListener("beforeunload",(()=>{window.scrollTo(0,0)}))})();